    //临时私有方法，完全为模拟服务器get()方法服务，获取uri中前一部分
    private String getURL(String key) {
        String result = "";
        String list = Constant.SE_LIST_SEARCH_URL;
        String map = Constant.SE_MAP_SEARCH_URL;
        String advs = Constant.SE_ADVANCED_SEARCH_URL;
        String suggest = Constant.SE_GET_SUGGESTION_URL;
        String line = Constant.LG_MARKLINE_SEARCH_URL;
        String login = Constant.WUM_LOGIN_URL;
        String register = Constant.WUM_REGISTER_URL;
        String emailCheck = Constant.WUM_UNIQUE_EMAIL_CHECK_URL;
        String nameCheck = Constant.WUM_UNIQUE_USERNAME_CHECK_URL;
        String point = Constant.SE_MARKPOINT_SEARCH_URL;
        String forgetPwd = Constant.WUM_FORGET_PASSWORD;
        if ("list".equals(key)) {
            result = list.substring(0, list.indexOf("{"));
        } else if ("map".equals(key)) {
            result = map.substring(0, map.indexOf("{"));
        } else if ("advs".equals(key)) {
            result = advs.substring(0, advs.indexOf("{"));
        } else if ("suggest".equals(key)) {
            result = suggest;
        } else if ("line".equals(key)) {
            result = line.substring(0, line.indexOf("?"));
        } else if ("point".equals(key)) {
            result = point.substring(0, point.indexOf("{"));
        } else if ("login".equals(key)) {
            result = login;
        } else if ("register".equals(key)) {
            result = register;
        } else if ("emailCheck".equals(key)) {
            result = emailCheck;
        } else if ("nameCheck".equals(key)) {
            result = nameCheck;
        } else if ("forgetPwd".equals(key)) {
            result = forgetPwd;
        }
        return result;
    }

